INSERT INTO yunjiu.article (created_at, updated_at, title, description, content, cover, publish_at, short_story, status, auth_way, auth_reprinted, tag, article_memo, home_page_no, category_id, creator_id, mark) VALUES ('2017-03-21 07:23:26.825071', '2017-03-21 09:05:52.871793', '王新国：白酒销售从“5”到“6”，“酒业现象”值得深入研究', '', '<section data-role="outer" label="Powered by 135editor.com" style="font-family: 微软雅黑; font-size: 16px;"><section data-role="outer" label="Powered by 135editor.com" style="font-family:微软雅黑;font-size:16px;"><section data-role="outer" label="Powered by 135editor.com" style="font-family: 微软雅黑; font-size: 16px; box-sizing: border-box;"><section data-role="outer" label="Powered by 135editor.com" style="font-family: 微软雅黑; font-size: 16px; box-sizing: border-box;"><section data-role="outer" label="Powered by 135editor.com" style="font-family: 微软雅黑; font-size: 16px; box-sizing: border-box;"><section data-role="outer" label="Powered by 135editor.com" style="font-family: 微软雅黑; font-size: 16px; box-sizing: border-box;"><section data-role="outer" label="Powered by 135editor.com" style="font-family: 微软雅黑; font-size: 16px; box-sizing: border-box;"><section data-role="outer" label="Powered by 135editor.com" style="font-family: 微软雅黑; font-size: 16px; box-sizing: border-box;"><section class="_135editor" data-tools="135编辑器" data-id="87922" style="position: relative; border: 0px none; padding: 0px; box-sizing: border-box;"><section><br/></section></section><section data-tools="135编辑器" data-id="16" data-color="#FDFFE5" data-custom="#FDFFE5" style="box-sizing: border-box;"><section style="box-sizing: border-box;"><section data-tools="135编辑器" data-id="16" data-color="#f8f8f8" data-custom="#f8f8f8" style="box-sizing: border-box;"><section style="box-sizing: border-box;"><section style="box-sizing: border-box;"><section style="box-sizing: border-box;"><section style="box-sizing: border-box;"><section data-tools="135编辑器" data-id="16" data-color="#FDFFE5" data-custom="#FDFFE5" style="box-sizing: border-box;"><section style="box-sizing: border-box;"><section class="_135editor" data-tools="135编辑器" data-id="16" data-color="#f8f8f8" data-custom="#f8f8f8" style="border: 0px none; padding: 0px; position: relative; box-sizing: border-box;"><section class="135brush layout" style="margin: 10px auto; padding: 15px; border-color: rgb(253, 255, 229); border-top-left-radius: 4px; border-top-right-radius: 4px; border-bottom-right-radius: 4px; border-bottom-left-radius: 4px; box-sizing: border-box; background-color: rgb(248, 248, 248);"><section style="box-sizing: border-box;"><section class="_135editor" data-tools="135编辑器" data-id="88167" style="border: 0px none; padding: 0px; box-sizing: border-box;"><section style="box-sizing: border-box;"><section data-role="paragraph" class="_135editor" style="border: 0px none; padding: 0px; position: relative; box-sizing: border-box;"><p style="white-space: normal; text-align: left;"><strong style="text-align: justify; color: rgb(211, 229, 0); line-height: 28px;"><span style="color: #464C56;">文 | 云酒团队</span></strong><span style="color: #464C56; line-height: 25.6000003814697px; text-align: justify; white-space: pre-wrap;">（微信ID：YJTT2016）</span></p></section><p style="white-space: normal;"><br/></p></section></section><section class="_135editor" data-tools="135编辑器" data-id="86434" style="border: 0px none; padding: 0px; position: relative; box-sizing: border-box;"><section style="margin: 5px auto; box-sizing: border-box;"><p style="line-height: 1.75em; white-space: normal;"><span style="color: #5E5E5E; line-height: 25.6000003814697px; text-align: justify; white-space: pre-wrap;">3月21日，</span><span style="color: #5E5E5E; line-height: 25.6000003814697px; text-align: justify; white-space: pre-wrap;">以“实业与资本中国新经济的酒业力量”为主题的2017云酒头条财经论坛暨中国酒业上市公司年度峰会在成都举行，本次峰会由中国酒类流通协会主办，云酒传媒承办，全国糖酒商品交易会办公室特别支持，并得到了新浪财经、证券日报、亲见传媒、中国酒业论坛的全程支持，是中国酒业卓越上市公司的首次集体聚会和亮相。</span></p><p><br/></p><p><span style="color: #5E5E5E; line-height: 25.6000003814697px; text-align: justify;">中国酒类流通协会会长王新国在会上表示2016年，规模和规模以上白酒企业销售收入首次超过6000亿元大关，增幅达到10.07%。从“5”到“6”，意味着酒业在各个行业领域中实现了率先复苏。</span></p><p><br/></p><p><span style="color: #5E5E5E; line-height: 25.6000003814697px; text-align: justify;">王新国认为，酒业强势发展的具体表现，包括各大酒企的市场表现有了很大起色，同时在二级市场上，酒类板块涨势汹汹，受到投资者的广泛关注和追捧。“在当前国内经济低迷的外部环境下，酒业为什么能率先反弹，酒业上市公司为何表现如此突出，我认为都是值得深入研究和探讨的问题。”</span></p><p style="line-height: 1.75em; white-space: normal;"><br/></p></section></section></section></section></section><section data-role="paragraph" class="_135editor" style="border: 0px none; padding: 0px; box-sizing: border-box;"><p style="white-space: normal;"><br/></p></section></section></section></section></section></section></section></section></section></section><section data-role="paragraph" class="_135editor" style="border: 0px none; padding: 0px; box-sizing: border-box;"><p style="white-space: normal;"><br/></p><p style="white-space: normal;"><br/></p><p style="white-space: normal;"><br/></p></section></section></section></section></section></section></section></section></section>', 'wang.jpg', '2017-03-21 17:00:00.000000', 1, 2, 1, 0, 0, null, 0, 2, 1, '');
INSERT INTO yunjiu.article (created_at, updated_at, title, description, content, cover, publish_at, short_story, status, auth_way, auth_reprinted, tag, article_memo, home_page_no, category_id, creator_id, mark) VALUES ('2017-03-21 07:42:08.149819', '2017-03-21 09:05:45.192156', '秦书尧：酒业为实体经济发展找到“答案”', '', '<section data-role="outer" label="Powered by 135editor.com" style="font-family: 微软雅黑;"><section data-role="outer" label="Powered by 135editor.com"><section data-role="outer" label="Powered by 135editor.com"><section data-role="outer" label="Powered by 135editor.com" style="box-sizing: border-box;"><section data-role="outer" label="Powered by 135editor.com" style="box-sizing: border-box;"><section data-role="outer" label="Powered by 135editor.com" style="box-sizing: border-box;"><section data-role="outer" label="Powered by 135editor.com" style="box-sizing: border-box;"><section data-role="outer" label="Powered by 135editor.com" style="box-sizing: border-box;"><section data-role="outer" label="Powered by 135editor.com" style="box-sizing: border-box;"><section data-tools="135编辑器" data-id="16" data-color="#FDFFE5" data-custom="#FDFFE5" style="box-sizing: border-box;"><section style="box-sizing: border-box;"><section data-tools="135编辑器" data-id="16" data-color="#f8f8f8" data-custom="#f8f8f8" style="box-sizing: border-box;"><section style="box-sizing: border-box;"><section style="box-sizing: border-box;"><section style="box-sizing: border-box;"><section style="box-sizing: border-box;"><section data-tools="135编辑器" data-id="16" data-color="#FDFFE5" data-custom="#FDFFE5" style="box-sizing: border-box;"><section style="box-sizing: border-box;"><section class="_135editor" data-tools="135编辑器" data-id="16" data-color="#f8f8f8" data-custom="#f8f8f8" style="border: 0px none; padding: 0px; position: relative; box-sizing: border-box;"><section class="135brush layout" style="margin: 10px auto; padding: 15px; border-color: rgb(253, 255, 229); border-top-left-radius: 4px; border-top-right-radius: 4px; border-bottom-right-radius: 4px; border-bottom-left-radius: 4px; box-sizing: border-box; background-color: rgb(248, 248, 248);"><section style="box-sizing: border-box;"><section class="_135editor" data-tools="135编辑器" data-id="88167" style="border: 0px none; padding: 0px; box-sizing: border-box; position: relative;"><section style="box-sizing: border-box;"><section data-role="paragraph" class="_135editor" style="border: 0px none; padding: 0px; position: relative; box-sizing: border-box;"><p style="white-space: normal;"><strong style="text-align: justify; color: rgb(211, 229, 0); line-height: 28px;"><span style="color: #464C56;">文 | 云酒团队</span></strong><span style="color: #464C56; line-height: 25.6000003814697px; text-align: justify; white-space: pre-wrap;">（微信ID：YJTT2016）</span></p></section><p style="white-space: normal;"><br/></p></section></section><section class="_135editor" data-tools="135编辑器" data-id="86434" style="border: 0px none; padding: 0px; position: relative; box-sizing: border-box;"><section style="margin: 5px auto; box-sizing: border-box;"><p style="line-height: 1.75em; white-space: normal;"><span style="color: #5E5E5E; line-height: 25.6000003814697px; text-align: justify;">3月21日，以“实业与资本中国新经济的酒业力量”为主题的2017云酒头条财经论坛暨中国酒业上市公司年度峰会在成都举行，本次会议是中国酒业卓越上市公司的首次集体聚会和亮相，其对实体经济发展具有重要的样本意义。</span></p><p style="line-height: 1.75em; white-space: normal;"><br/></p><p style="line-height: 1.75em; white-space: normal;"><span style="color: #5E5E5E; line-height: 25.6000003814697px; text-align: justify;">中国酒类流通协会秘书长秦书尧认为，中国实体经济目前所面临的几个核心问题，在酒业身上都能找到解决方案。比如高税负问题，一度对白酒行业造成重创，而经过适应调整，酒行业已经找到了相对有效的化解办法。面对虚拟经济同样如此，酒企与电商之间曾经矛盾尖锐，现在已结合得非常紧密。</span></p><p style="line-height: 1.75em; white-space: normal;"><span style="color: #5E5E5E; line-height: 25.6000003814697px; text-align: justify;">所以说，酒业是振兴实体经济的一个优秀样本，占据酒业重要比重的上市公司们，如果能够主动发声，积极作为，为实体经济“以酒解忧”，那将是具有时代使命意义的伟大表现。</span></p></section></section></section></section></section></section></section></section></section></section></section></section></section></section><section data-role="paragraph" class="_135editor" style="border: 0px none; padding: 0px; box-sizing: border-box;"><p style="white-space: normal;"><br/></p><p style="white-space: normal;"><br/></p><p style="white-space: normal;"><br/></p></section></section></section></section></section></section></section></section></section></section><p><br/></p>', '20170321153755.jpg', '2017-03-21 17:00:00.000000', 1, 2, 1, 0, 0, null, 0, 2, 1, '');
INSERT INTO yunjiu.article (created_at, updated_at, title, description, content, cover, publish_at, short_story, status, auth_way, auth_reprinted, tag, article_memo, home_page_no, category_id, creator_id, mark) VALUES ('2017-03-21 08:16:28.505598', '2017-04-12 15:46:34.843057', '姚景源：规上工业企业去年每百元产值，物流成本高达30%-40%', '', '<section data-role="outer" label="Powered by 135editor.com" style="font-family: 微软雅黑;"><section data-role="outer" label="Powered by 135editor.com"><section data-role="outer" label="Powered by 135editor.com"><section data-role="outer" label="Powered by 135editor.com" style="box-sizing: border-box;"><section data-role="outer" label="Powered by 135editor.com" style="box-sizing: border-box;"><section data-role="outer" label="Powered by 135editor.com" style="box-sizing: border-box;"><section data-role="outer" label="Powered by 135editor.com" style="box-sizing: border-box;"><section data-role="outer" label="Powered by 135editor.com" style="box-sizing: border-box;"><section data-role="outer" label="Powered by 135editor.com" style="box-sizing: border-box;"><section data-tools="135编辑器" data-id="16" data-color="#FDFFE5" data-custom="#FDFFE5" style="box-sizing: border-box;"><section style="box-sizing: border-box;"><section data-tools="135编辑器" data-id="16" data-color="#f8f8f8" data-custom="#f8f8f8" style="box-sizing: border-box;"><section style="box-sizing: border-box;"><section style="box-sizing: border-box;"><section style="box-sizing: border-box;"><section style="box-sizing: border-box;"><section data-tools="135编辑器" data-id="16" data-color="#FDFFE5" data-custom="#FDFFE5" style="box-sizing: border-box;"><section style="box-sizing: border-box;"><section class="_135editor" data-tools="135编辑器" data-id="16" data-color="#f8f8f8" data-custom="#f8f8f8" style="border: 0px none; padding: 0px; position: relative; box-sizing: border-box;"><section class="135brush layout" style="margin: 10px auto; padding: 15px; border-color: rgb(253, 255, 229); border-top-left-radius: 4px; border-top-right-radius: 4px; border-bottom-right-radius: 4px; border-bottom-left-radius: 4px; box-sizing: border-box; background-color: rgb(248, 248, 248);"><section style="box-sizing: border-box;"><section class="_135editor" data-tools="135编辑器" data-id="88167" style="border: 0px none; padding: 0px; box-sizing: border-box; position: relative;"><section style="box-sizing: border-box;"><section data-role="paragraph" class="_135editor" style="border: 0px none; padding: 0px; position: relative; box-sizing: border-box;"><p style="white-space: normal;"><strong style="text-align: justify; color: rgb(211, 229, 0); line-height: 28px;"><span style="color: #464C56;">文 | 云酒团队</span></strong><span style="color: #464C56; line-height: 25.6000003814697px; text-align: justify; white-space: pre-wrap;">（微信ID：YJTT2016）</span></p><p style="white-space: normal;"><span style="color: #464C56; line-height: 25.6000003814697px; text-align: justify; white-space: pre-wrap;"><br/></span></p></section></section></section><section class="_135editor" data-tools="135编辑器" data-id="86434" style="border: 0px none; padding: 0px; position: relative; box-sizing: border-box;"><section style="margin: 5px auto; box-sizing: border-box;"><p style="white-space: normal;"><span style="color: #464C56; line-height: 25.6000003814697px; text-align: justify; white-space: pre-wrap;">在3月21日的2017云酒头条财经论坛暨中国酒业上市公司年度峰会上，著名经济学家、国务院参事室特约研究员姚景源结合当前宏观经济形势，精彩解读了酒业发展前景。</span></p><p style="white-space: normal;"><span style="color: rgb(70, 76, 86); line-height: 25.6000003814697px; text-align: justify; white-space: pre-wrap;"><br/></span></p><p style="white-space: normal;"><span style="color: rgb(70, 76, 86); line-height: 25.6000003814697px; text-align: justify; white-space: pre-wrap;">姚景源表示，在过去一年，尽管中国经济内外环境十分严峻，各种黑天鹅层出不穷，但还是取得了来自不易的成绩。姚景源所说的“来之不易”有四个具体体现，一是经销下行趋势缓冲趋稳，尽管尚不能断言见底，但已有明显好转趋势。另外，国内物价水平表现合乎预期，2%的物价上涨水平略低于预期值。第三是工业品出厂价格和企业利润水平由负转正，带来了积极有利的经济预期，将有力拉动投资和消费。在就业方面，在当前社会形势下，如果每年新增就业低于1000万，将无法满足实际需求，而去年国内实际实现酒业1300多万，这一成就同样来之不易。</span></p><p style="white-space: normal;"><span style="color: #464C56; line-height: 25.6000003814697px; text-align: justify; white-space: pre-wrap;"><br/></span></p><p style="white-space: normal;"><span style="color: #464C56; line-height: 25.6000003814697px; text-align: justify; white-space: pre-wrap;">基于去年形势，今年国内的主要经济任务仍是“三去一降一补”，包括以市场和法制手段调整产能，比如设置能耗和污染等硬性指标的门槛。另外，仍要大力降低企业成本，包括降低税费，降低用电、年息、物流等各种成本。姚景源表示，统计数据显示，全国规模以上工业企业去年每实现一百元产值，其中成本高达85.76元，物流成本更是占到30%至40%，而发达国家物流成本仅占10至15%。如果能够有效降低企业成本，将对企业发展形成重大利好。</span></p><p style="white-space: normal;"><span style="color: #464C56; line-height: 25.6000003814697px; text-align: justify; white-space: pre-wrap;"><br/></span></p><p style="white-space: normal;"><span style="color: #464C56; line-height: 25.6000003814697px; text-align: justify; white-space: pre-wrap;">姚景源认为，酒业“补短板”的关键是发挥文化价值，去年出境旅游人次达1亿2千万，人均消费额超过1万元，这表明中国消费者具有很强的消费能力和消费意愿，关键是如何满足消费者的新生消费需求。在姚景源看来，酒类消费不仅是简单的消费行为，更是一种文化现象，“今天的酒类回升发展，是消费升级的重要标志，通过文化支撑，满足老百姓的实际需求，酒业未来潜力和空间仍很巨大。”</span></p></section></section></section></section></section></section></section></section></section></section></section></section></section></section></section></section></section></section></section></section></section></section></section>', 'yao.jpg', '2017-03-21 17:00:00.000000', 1, 2, 1, 0, 0, null, 1, 1, 1, '');
INSERT INTO yunjiu.article (created_at, updated_at, title, description, content, cover, publish_at, short_story, status, auth_way, auth_reprinted, tag, article_memo, home_page_no, category_id, creator_id, mark) VALUES ('2017-03-21 08:40:34.214091', '2017-03-21 09:05:32.763876', '朱昭鑫：洋河多年来的追求一直放在“实业”上', '', '<section data-role="outer" label="Powered by 135editor.com" style="font-family: 微软雅黑;"><section data-role="outer" label="Powered by 135editor.com"><section data-role="outer" label="Powered by 135editor.com"><section data-role="outer" label="Powered by 135editor.com" style="box-sizing: border-box;"><section data-role="outer" label="Powered by 135editor.com" style="box-sizing: border-box;"><section data-role="outer" label="Powered by 135editor.com" style="box-sizing: border-box;"><section data-role="outer" label="Powered by 135editor.com" style="box-sizing: border-box;"><section data-role="outer" label="Powered by 135editor.com" style="box-sizing: border-box;"><section data-role="outer" label="Powered by 135editor.com" style="box-sizing: border-box;"><section data-tools="135编辑器" data-id="16" data-color="#FDFFE5" data-custom="#FDFFE5" style="box-sizing: border-box;"><section style="box-sizing: border-box;"><section data-tools="135编辑器" data-id="16" data-color="#f8f8f8" data-custom="#f8f8f8" style="box-sizing: border-box;"><section style="box-sizing: border-box;"><section style="box-sizing: border-box;"><section style="box-sizing: border-box;"><section style="box-sizing: border-box;"><section data-tools="135编辑器" data-id="16" data-color="#FDFFE5" data-custom="#FDFFE5" style="box-sizing: border-box;"><section style="box-sizing: border-box;"><section class="_135editor" data-tools="135编辑器" data-id="16" data-color="#f8f8f8" data-custom="#f8f8f8" style="border: 0px none; padding: 0px; position: relative; box-sizing: border-box;"><section class="135brush layout" style="margin: 10px auto; padding: 15px; border-color: rgb(253, 255, 229); border-top-left-radius: 4px; border-top-right-radius: 4px; border-bottom-right-radius: 4px; border-bottom-left-radius: 4px; box-sizing: border-box; background-color: rgb(248, 248, 248);"><section style="box-sizing: border-box;"><section class="_135editor" data-tools="135编辑器" data-id="88167" style="border: 0px none; padding: 0px; box-sizing: border-box; position: relative;"><section style="box-sizing: border-box;"><section data-role="paragraph" class="_135editor" style="border: 0px none; padding: 0px; position: relative; box-sizing: border-box;"><p style="white-space: normal;"><strong style="text-align: justify; color: rgb(211, 229, 0); line-height: 28px;"><span style="color: #464C56;">文 | 云酒团队</span></strong><span style="color: #464C56; line-height: 25.6000003814697px; text-align: justify; white-space: pre-wrap;">（微信ID：YJTT2016）</span></p></section><p style="white-space: normal;"><br/></p></section></section><section class="_135editor" data-tools="135编辑器" data-id="86434" style="border: 0px none; padding: 0px; position: relative; box-sizing: border-box;"><section style="margin: 5px auto; box-sizing: border-box;"><p style="white-space: normal;"><span style="color: #464C56; line-height: 25.6000003814697px; text-align: justify;">在3月21日的2017云酒头条财经论坛暨中国酒业上市公司年度峰会的论坛环节，各大酒业上市公司代表就当前行业形势和企业发展等问题，进行了精彩交流。</span><br/></p><p style="white-space: normal;"><br/></p><p style="text-align: justify; line-height: 1.75em; white-space: normal;"><span style="color: rgb(70, 76, 86); line-height: 25.6000003814697px; white-space: pre-wrap;">江苏洋河酒厂股份有限公司市场总监朱昭鑫表示，洋河整个公司上下，从高层领导到基层员工，多年来的追求一直放在“实业”上，而为了做强实业，提升企业价值，洋河主要从三个方面实施创新。</span></p><p style="white-space: normal;"><br/></p><p style="text-align: justify; line-height: 1.75em; white-space: normal;"><span style="color: rgb(70, 76, 86); line-height: 25.6000003814697px; white-space: pre-wrap;">一是对品质长期不懈的坚守和传承。“在坚守和传承过程中，洋河没有保持一成不变，洋河的品质创新，始终围绕消费者需求这个核心，全力为消费者提供健康和良好体验的产品品质。”</span></p><p style="white-space: normal;"><br/></p><p style="text-align: justify; line-height: 1.75em; white-space: normal;"><span style="color: rgb(70, 76, 86); line-height: 25.6000003814697px; white-space: pre-wrap;">二是对品牌形象的创新塑造。“洋河的品牌塑造和品牌传播，取得了外界有目共睹的创新效果，对企业的整体发展成就具有很重要的意义。”</span></p><p style="white-space: normal;"><br/></p><p style="text-align: justify; line-height: 1.75em; white-space: normal;"><span style="color: rgb(70, 76, 86); line-height: 25.6000003814697px; white-space: pre-wrap;">尤其重要的第三点是内部管理机制的激活创新，以及公司内部组织架构的调整。朱昭鑫表示，在洋河的不同发展阶段，根据企业实际情况，其在人才引进、人才激励等方面，始终在创新优化，为企业长远发展提供有力保障。</span></p></section></section></section></section></section></section></section></section></section></section></section></section></section></section></section></section></section></section></section></section></section></section></section>', '20170321163357.jpg', '2017-03-21 17:00:00.000000', 1, 2, 1, 0, 0, null, 0, 1, 1, '');
INSERT INTO yunjiu.article (created_at, updated_at, title, description, content, cover, publish_at, short_story, status, auth_way, auth_reprinted, tag, article_memo, home_page_no, category_id, creator_id, mark) VALUES ('2017-03-21 08:52:59.163000', '2017-03-21 09:01:52.012190', '倪从春：今世缘不是“全能冠军”，要在“单项”出彩，省外市场争取过20%', '', '<section data-role="outer" label="Powered by 135editor.com" style="font-family: 微软雅黑;"><section data-role="outer" label="Powered by 135editor.com"><section data-role="outer" label="Powered by 135editor.com"><section data-role="outer" label="Powered by 135editor.com" style="box-sizing: border-box;"><section data-role="outer" label="Powered by 135editor.com" style="box-sizing: border-box;"><section data-role="outer" label="Powered by 135editor.com" style="box-sizing: border-box;"><section data-role="outer" label="Powered by 135editor.com" style="box-sizing: border-box;"><section data-role="outer" label="Powered by 135editor.com" style="box-sizing: border-box;"><section data-role="outer" label="Powered by 135editor.com" style="box-sizing: border-box;"><section data-tools="135编辑器" data-id="16" data-color="#FDFFE5" data-custom="#FDFFE5" style="box-sizing: border-box;"><section style="box-sizing: border-box;"><section data-tools="135编辑器" data-id="16" data-color="#f8f8f8" data-custom="#f8f8f8" style="box-sizing: border-box;"><section style="box-sizing: border-box;"><section style="box-sizing: border-box;"><section style="box-sizing: border-box;"><section style="box-sizing: border-box;"><section data-tools="135编辑器" data-id="16" data-color="#FDFFE5" data-custom="#FDFFE5" style="box-sizing: border-box;"><section style="box-sizing: border-box;"><section class="_135editor" data-tools="135编辑器" data-id="16" data-color="#f8f8f8" data-custom="#f8f8f8" style="border: 0px none; padding: 0px; position: relative; box-sizing: border-box;"><section class="135brush layout" style="margin: 10px auto; padding: 15px; border-color: rgb(253, 255, 229); border-top-left-radius: 4px; border-top-right-radius: 4px; border-bottom-right-radius: 4px; border-bottom-left-radius: 4px; box-sizing: border-box; background-color: rgb(248, 248, 248);"><section style="box-sizing: border-box;"><section class="_135editor" data-tools="135编辑器" data-id="88167" style="border: 0px none; padding: 0px; box-sizing: border-box; position: relative;"><section style="box-sizing: border-box;"><section data-role="paragraph" class="_135editor" style="border: 0px none; padding: 0px; position: relative; box-sizing: border-box;"><p style="white-space: normal; text-align: justify;"><strong style="text-align: justify; color: rgb(211, 229, 0); line-height: 28px;"><span style="color: #464C56;">文 | 云酒团队</span></strong><span style="color: #464C56; line-height: 25.6000003814697px; text-align: justify; white-space: pre-wrap;">（微信ID：YJTT2016）</span></p></section><p style="white-space: normal;"><br/></p></section></section><section class="_135editor" data-tools="135编辑器" data-id="86434" style="border: 0px none; padding: 0px; position: relative; box-sizing: border-box;"><section style="margin: 5px auto; box-sizing: border-box;"><p style="text-align: justify; white-space: normal;"><span style="color: rgb(70, 76, 86); line-height: 25.6000003814697px;">在3月21日的2017云酒头条财经论坛暨中国酒业上市公司年度峰会的论坛环节，各大酒业上市公司代表就当前行业形势和企业发展等问题，进行了精彩交流。</span></p><p style="white-space: normal;"><br/></p><p style="text-align: justify; white-space: normal;"><span style="color: rgb(70, 76, 86); line-height: 25.6000003814697px;">江苏今世缘酒业股份有限公司党委副书记、副总经理倪从春表示，在江苏乃至全国市场，洋河是值得尊敬和学习的榜样，“今世缘不是‘全能冠军’，必须在‘单项’上有突出的亮点。” 比如今世缘对智能化、机械化生产的研究应用，以及智能化电子水平，在酒业处在最前列地位，而针对传统工具和现代科技的融合，今世缘仍在做进一步的探索尝试。</span></p><p style="white-space: normal;"><br/></p><p style="text-align: justify; white-space: normal;"><span style="color: rgb(70, 76, 86); line-height: 25.6000003814697px;">倪从春表示，今世缘目前约90%的市场份额在江苏省内，在省外体量较小。“我们有一个小目标，就是省外市场份额能够超20%，甚至更高。”为此，今世缘制定了系统的市场规划和战略，即省内进一步精耕细作，省外重点突破，“我们现在的区域战略是‘1+2+4’，1就是一级市场，2是两个形象市场，北京和上海，4是河南、安徽、山东、浙江，最后是捕捉一些机会市场。”</span></p></section></section></section></section></section></section></section></section></section></section></section></section></section></section></section></section></section></section></section></section></section></section></section>', 'ni.jpg', '2017-03-21 17:01:00.000000', 1, 2, 1, 0, 0, null, 0, 1, 1, '');
INSERT INTO yunjiu.article (created_at, updated_at, title, description, content, cover, publish_at, short_story, status, auth_way, auth_reprinted, tag, article_memo, home_page_no, category_id, creator_id, mark) VALUES ('2017-03-21 09:12:24.332127', '2017-03-21 09:16:03.746111', '茅台董秘樊宁屏：酒业对实业发展的贡献应被充分肯定', '', '<section data-role="outer" label="Powered by 135editor.com" style="font-family: 微软雅黑;"><section data-role="outer" label="Powered by 135editor.com"><section data-role="outer" label="Powered by 135editor.com"><section data-role="outer" label="Powered by 135editor.com" style="box-sizing: border-box;"><section data-role="outer" label="Powered by 135editor.com" style="box-sizing: border-box;"><section data-role="outer" label="Powered by 135editor.com" style="box-sizing: border-box;"><section data-role="outer" label="Powered by 135editor.com" style="box-sizing: border-box;"><section data-role="outer" label="Powered by 135editor.com" style="box-sizing: border-box;"><section data-role="outer" label="Powered by 135editor.com" style="box-sizing: border-box;"><section data-tools="135编辑器" data-id="16" data-color="#FDFFE5" data-custom="#FDFFE5" style="box-sizing: border-box;"><section style="box-sizing: border-box;"><section data-tools="135编辑器" data-id="16" data-color="#f8f8f8" data-custom="#f8f8f8" style="box-sizing: border-box;"><section style="box-sizing: border-box;"><section style="box-sizing: border-box;"><section style="box-sizing: border-box;"><section style="box-sizing: border-box;"><section data-tools="135编辑器" data-id="16" data-color="#FDFFE5" data-custom="#FDFFE5" style="box-sizing: border-box;"><section style="box-sizing: border-box;"><section class="_135editor" data-tools="135编辑器" data-id="16" data-color="#f8f8f8" data-custom="#f8f8f8" style="border: 0px none; padding: 0px; position: relative; box-sizing: border-box;"><section class="135brush layout" style="margin: 10px auto; padding: 15px; border-color: rgb(253, 255, 229); border-top-left-radius: 4px; border-top-right-radius: 4px; border-bottom-right-radius: 4px; border-bottom-left-radius: 4px; box-sizing: border-box; background-color: rgb(248, 248, 248);"><section style="box-sizing: border-box;"><section class="_135editor" data-tools="135编辑器" data-id="88167" style="border: 0px none; padding: 0px; box-sizing: border-box; position: relative;"><section style="box-sizing: border-box;"><section data-role="paragraph" class="_135editor" style="border: 0px none; padding: 0px; position: relative; box-sizing: border-box;"><p style="white-space: normal;"><strong style="text-align: justify; color: rgb(211, 229, 0); line-height: 28px;"><span style="color: #464C56;">文 | 云酒团队</span></strong><span style="color: #464C56; line-height: 25.6000003814697px; text-align: justify; white-space: pre-wrap;">（微信ID：YJTT2016）</span></p></section><p style="white-space: normal;"><br/></p></section></section><section class="_135editor" data-tools="135编辑器" data-id="86434" style="border: 0px none; padding: 0px; position: relative; box-sizing: border-box;"><section style="margin: 5px auto; box-sizing: border-box;"><p style="text-align: justify; white-space: normal;"><span style="color: #464C56; line-height: 25.6000003814697px;">3月21日，在2017云酒头条财经论坛暨中国酒业上市公司年度峰会上，贵州茅台酒股份有限公司总经理助理、董事会秘书樊宁屏被评为中国酒业金牌董秘，她在获奖感言中表示，非常荣幸领取这个奖项，首先要感谢公司和领导同事的关心帮助，同时要感谢广大投资人和媒体的认可，“是大家热情、周到的帮助，才使我不断进步”，还要感谢云酒传媒创造这样一个宝贵机会，肯定和展示了酒业对实业发展所做出的积极贡献。</span><br/></p><p style="text-align: justify; white-space: normal;"><span style="color: #464C56; line-height: 25.6000003814697px;"><br/></span></p><p style="text-align: center; white-space: normal;"><img src="http://ojv4b1res.bkt.clouddn.com/media/20170321171534_20170321091601_338.jpg" title="" alt="20170321171534.jpg"/></p><p style="white-space: normal;"><br/></p><p style="text-align: justify; white-space: normal;"><span style="color: rgb(70, 76, 86); line-height: 25.6000003814697px;">“今天获得中国酒业金牌董秘这个荣誉，对我来讲是肯定，更是期待，”樊宁屏表示，在今后工作中一定会将压力转为动力，为企业创造更多价值。</span></p></section></section></section></section></section></section></section></section></section></section></section></section></section></section></section></section></section></section></section></section></section></section></section>', '20170321171403.jpg', '2017-03-21 17:10:00.000000', 1, 2, 1, 0, 0, null, 0, 1, 1, '');
INSERT INTO yunjiu.article (created_at, updated_at, title, description, content, cover, publish_at, short_story, status, auth_way, auth_reprinted, tag, article_memo, home_page_no, category_id, creator_id, mark) VALUES ('2017-03-21 09:28:38.901147', '2017-03-21 09:28:38.901289', '五粮液营销管理部部长陈家林：与品质相比，其他都是从属管理', '', '<section data-role="outer" label="Powered by 135editor.com" style="font-family: 微软雅黑;"><section data-role="outer" label="Powered by 135editor.com"><section data-role="outer" label="Powered by 135editor.com"><section data-role="outer" label="Powered by 135editor.com" style="box-sizing: border-box;"><section data-role="outer" label="Powered by 135editor.com" style="box-sizing: border-box;"><section data-role="outer" label="Powered by 135editor.com" style="box-sizing: border-box;"><section data-role="outer" label="Powered by 135editor.com" style="box-sizing: border-box;"><section data-role="outer" label="Powered by 135editor.com" style="box-sizing: border-box;"><section data-role="outer" label="Powered by 135editor.com" style="box-sizing: border-box;"><section data-tools="135编辑器" data-id="16" data-color="#FDFFE5" data-custom="#FDFFE5" style="box-sizing: border-box;"><section style="box-sizing: border-box;"><section data-tools="135编辑器" data-id="16" data-color="#f8f8f8" data-custom="#f8f8f8" style="box-sizing: border-box;"><section style="box-sizing: border-box;"><section style="box-sizing: border-box;"><section style="box-sizing: border-box;"><section style="box-sizing: border-box;"><section data-tools="135编辑器" data-id="16" data-color="#FDFFE5" data-custom="#FDFFE5" style="box-sizing: border-box;"><section style="box-sizing: border-box;"><section class="_135editor" data-tools="135编辑器" data-id="16" data-color="#f8f8f8" data-custom="#f8f8f8" style="border: 0px none; padding: 0px; position: relative; box-sizing: border-box;"><section class="135brush layout" style="margin: 10px auto; padding: 15px; border-color: rgb(253, 255, 229); border-top-left-radius: 4px; border-top-right-radius: 4px; border-bottom-right-radius: 4px; border-bottom-left-radius: 4px; box-sizing: border-box; background-color: rgb(248, 248, 248);"><section style="box-sizing: border-box;"><section class="_135editor" data-tools="135编辑器" data-id="88167" style="border: 0px none; padding: 0px; box-sizing: border-box; position: relative;"><section style="box-sizing: border-box;"><section data-role="paragraph" class="_135editor" style="border: 0px none; padding: 0px; position: relative; box-sizing: border-box;"><p style="white-space: normal;"><strong style="text-align: justify; color: rgb(211, 229, 0); line-height: 28px;"><span style="color: #464C56;">文 | 云酒团队</span></strong><span style="color: #464C56; line-height: 25.6000003814697px; text-align: justify; white-space: pre-wrap;">（微信ID：YJTT2016）</span></p></section><p style="white-space: normal;"><br/></p></section></section><section class="_135editor" data-tools="135编辑器" data-id="86434" style="border: 0px none; padding: 0px; position: relative; box-sizing: border-box;"><section style="margin: 5px auto; box-sizing: border-box;"><p style="text-align: justify; white-space: normal;"><span style="color: #464C56; line-height: 25.6000003814697px;">3月21日，在2017云酒头条财经论坛暨中国酒业上市公司年度峰会上，各大酒业上市公司代表就当前行业形势和企业发展等问题，进行了精彩交流。</span><br/></p><p style="white-space: normal;"><br/></p><p style="text-align: justify; line-height: 1.75em; white-space: normal;"><span style="color: #464C56; line-height: 25.6000003814697px;">五粮液营销管理部部长陈家林表示，公司于昨天刚刚召开经销商会议，在市场策略方面将进一步优化品牌架构和战略布局，同时拥抱创新，借助互联网的新思维、新理念，为企业创造更大价值。“但任何策略的基础都是品质，与品质相比，其他都是从属管理。”</span></p><p style="white-space: normal;"><br/></p><p style="text-align: justify; line-height: 1.75em; white-space: normal;"><span style="color: #464C56; line-height: 25.6000003814697px;">陈家林认为，资本市场和实体经济相当于硬币的两面，二者联系非常紧密。“在扩大市场，提升业绩的同时，五粮液下一步肯定还要加强公司治理，按照公众公司管理要求规范运营，主动回报市场和投资者。”</span></p></section></section></section></section></section></section></section></section></section></section></section></section></section></section></section></section></section></section></section></section></section></section></section>', '20170321171403_XK2ycKt.jpg', '2017-03-21 17:26:00.000000', 1, 2, 1, 0, 0, null, 0, 1, 1, '');
INSERT INTO yunjiu.article (created_at, updated_at, title, description, content, cover, publish_at, short_story, status, auth_way, auth_reprinted, tag, article_memo, home_page_no, category_id, creator_id, mark) VALUES ('2017-03-22 07:20:17.209912', '2017-03-22 07:20:17.209963', '经希军：通化每年12月9日将定为“北冰红日”', '', '<section data-role="outer" label="Powered by 135editor.com" style="font-family: 微软雅黑;"><section data-role="outer" label="Powered by 135editor.com"><section data-role="outer" label="Powered by 135editor.com"><section data-role="outer" label="Powered by 135editor.com" style="box-sizing: border-box;"><section data-role="outer" label="Powered by 135editor.com" style="box-sizing: border-box;"><section data-role="outer" label="Powered by 135editor.com" style="box-sizing: border-box;"><section data-role="outer" label="Powered by 135editor.com" style="box-sizing: border-box;"><section data-role="outer" label="Powered by 135editor.com" style="box-sizing: border-box;"><section data-role="outer" label="Powered by 135editor.com" style="box-sizing: border-box;"><section data-tools="135编辑器" data-id="16" data-color="#FDFFE5" data-custom="#FDFFE5" style="box-sizing: border-box;"><section style="box-sizing: border-box;"><section data-tools="135编辑器" data-id="16" data-color="#f8f8f8" data-custom="#f8f8f8" style="box-sizing: border-box;"><section style="box-sizing: border-box;"><section style="box-sizing: border-box;"><section style="box-sizing: border-box;"><section style="box-sizing: border-box;"><section data-tools="135编辑器" data-id="16" data-color="#FDFFE5" data-custom="#FDFFE5" style="box-sizing: border-box;"><section style="box-sizing: border-box;"><section class="_135editor" data-tools="135编辑器" data-id="16" data-color="#f8f8f8" data-custom="#f8f8f8" style="border: 0px none; padding: 0px; position: relative; box-sizing: border-box;"><section class="135brush layout" style="margin: 10px auto; padding: 15px; border-color: rgb(253, 255, 229); border-top-left-radius: 4px; border-top-right-radius: 4px; border-bottom-right-radius: 4px; border-bottom-left-radius: 4px; box-sizing: border-box; background-color: rgb(248, 248, 248);"><section style="box-sizing: border-box;"><section class="_135editor" data-tools="135编辑器" data-id="88167" style="border: 0px none; padding: 0px; box-sizing: border-box;"><section style="box-sizing: border-box;"><section data-role="paragraph" class="_135editor" style="border: 0px none; padding: 0px; position: relative; box-sizing: border-box;"><p style="white-space: normal;"><strong style="text-align: justify; color: rgb(211, 229, 0); line-height: 28px;"><span style="color: #464C56;">文 | 云酒团队</span></strong><span style="color: #464C56; line-height: 25.6000003814697px; text-align: justify; white-space: pre-wrap;">（微信ID：YJTT2016）</span></p></section><p style="white-space: normal;"><br/></p></section></section><section class="_135editor" data-tools="135编辑器" data-id="86434" style="border: 0px none; padding: 0px; position: relative; box-sizing: border-box;"><section style="margin: 5px auto; box-sizing: border-box;"><p style="text-align: justify; line-height: 1.75em; white-space: normal;"><span style="color: rgb(94, 94, 94); line-height: 25.6000003814697px;">3月22日，作为第96届全国糖酒商品交易会的亮点活动之一，“中国风土·鸭绿江谷”通化葡萄酒产区高峰论坛暨鸭绿江河谷五周年新闻发布会在四川成都举行，本次会议由中共通化市委、通化市人民政府主办，北京云酒传媒、中国酒类流通协会承办，由全国糖酒商品交易会办公室、中国食品工业协会葡萄酒专家委员会、中国酒业论坛全程支持。旨在面向全球酒商和广大媒体，展示通化葡萄酒产区形象，介绍鸭绿江河谷的风土特色与产品优势，以及共同探讨中国葡萄酒创新发展的未来方向。</span></p><p style="white-space: normal;"><br/></p><p style="text-align: justify; line-height: 1.75em; white-space: normal;"><span style="color: rgb(94, 94, 94); line-height: 25.6000003814697px;">中共通化市委常委、市委宣传部部长经希军表示，通化位于长白山腹地，山水相融，自然资源丰富。通化地处东北亚经济中心地带，与朝鲜隔鸭绿江相望，边境长203.5公里，是推进一带一路战略重要的互动连接带。通化是国内著名的葡萄酒城，有着了80多年的葡萄酒发展历史，1937年通化葡萄酒建厂，2001年通葡股份上市，2009年通天酒业上市，使通化成为首个拥有两家葡萄酒上市企业的地区。</span></p><p style="white-space: normal;"><br/></p><p style="text-align: justify; line-height: 1.75em; white-space: normal;"><span style="color: rgb(94, 94, 94); line-height: 25.6000003814697px;">据经希军介绍，近年来通化市委市政府重点发展葡萄酒产业，从葡萄的种植到葡萄酒加工，从品种的选育到产区确定，从酒庄建设到文化推广，都进行了全面的升级，目前通化拥有葡萄酒企业63户，发展酿酒葡萄5.6万亩，年产量5.8万吨。经希军表示，通化将于今年召开第二届山葡萄酒高峰论坛，并将每年12月9日定为“北冰红日”，以推广这一独特而优秀的葡萄品种，彰显当地风土特色和个性化价值。</span></p></section></section></section></section></section></section></section></section></section></section></section></section></section></section></section></section></section></section></section></section></section></section></section>', '通化背景板-01.jpg', '2017-03-22 15:18:00.000000', 1, 2, 1, 0, 0, null, 0, 2, 1, '');
